mapper.select:

select 

from ${church}.auth_group
where auth_set_idx = #{authSetIdx,jdbcType=INTEGER}
  and sort_no = #{sort_no,jdbcType=INTEGER}
  and auth_type_idx = #{auth_type_idx,jdbcType=INTEGER}

mapper.delete:

delete from ${church}.auth_group
where auth_set_idx = #{authSetIdx,jdbcType=INTEGER}
  and sort_no = #{sort_no,jdbcType=INTEGER}
  and auth_type_idx = #{auth_type_idx,jdbcType=INTEGER}

mapper.update:

update ${church}.auth_group

  
    value = #{value,jdbcType=INTEGER},
  
  
    reg_date = #{regDate,jdbcType=TIMESTAMP},
  

where auth_set_idx = #{authSetIdx,jdbcType=INTEGER}
  and sort_no = #{sort_no,jdbcType=INTEGER}
  and auth_type_idx = #{auth_type_idx,jdbcType=INTEGER}


update ${church}.auth_group
set value = #{value,jdbcType=INTEGER},
  reg_date = #{regDate,jdbcType=TIMESTAMP}
where auth_set_idx = #{authSetIdx,jdbcType=INTEGER}
  and sort_no = #{sort_no,jdbcType=INTEGER}
  and auth_type_idx = #{auth_type_idx,jdbcType=INTEGER}

mapper.insert:
insert into ${church}.auth_group (auth_set_idx,sort_no,auth_type_idx,value) select a2.auth_set_idx,g.sort_no,g.auth_type_idx,g.value from ${church}.auth_set a inner join ${church}.auth_set a2 on a2.year=#{nextYear} and a.auth_category_idx = a2.auth_category_idx and a.auth_set_kind = a2.auth_set_kind inner join ${church}.auth_group g on a.auth_set_idx = g.auth_set_idx where a.year=#{year}
and a2.k_part=#{kPartIdx}
group by a2.auth_set_idx,g.sort_no,g.auth_type_idx
