mapper.insert:
insert into ohjic.next_year_setting_status (church_code, year, k_part_idx, status, manager_idx) 
values (#{churchCode}, #{year}, #{kPartIdx}, #{status}, #{managerIdx})
on duplicate key update status=#{status}

mapper.select:
    select a.k_part_idx kPartIdx, k_part_name kPartName, if(b.status = 'Y' or c.k_part_idx is not null, 'Y', 'N') status, ifnull(c.group_count,0) groupCount,ifnull(f.group_count,0) oldGroupCount, ifnull(d.auth_set_count,0) authSetCount, ifnull(e.auth_set_count,0) oldAuthSetCount
    from ${church}.k_part a
	left outer join ohjic.next_year_setting_status b on b.church_code=#{churchCode} and b.year=#{year} and b.k_part_idx=a.k_part_idx
	left outer join (select year, k_part_idx, count(*) group_count from ${church}.kGroup group by year, k_part_idx) c on a.k_part_idx=c.k_part_idx and c.year=#{nextYear}
	left outer join (select year, k_part_idx, count(*) group_count from ${church}.kGroup group by year, k_part_idx) f on a.k_part_idx=f.k_part_idx and f.year=#{year}
	left outer join (select year, k_part k_part_idx, count(*) auth_set_count from ${church}.auth_set group by year, k_part) d on a.k_part_idx=d.k_part_idx and d.year=#{nextYear}
	left outer join (select year, k_part k_part_idx, count(*) auth_set_count from ${church}.auth_set group by year, k_part) e on a.k_part_idx=e.k_part_idx and e.year=#{year}
	where a.active=1
