mapper.update:

update ${church}.auth_in_charge
set auth_set_idx = #{authSetIdx,jdbcType=INTEGER},
  manager_idx = #{managerIdx,jdbcType=INTEGER},
  group_no = #{groupNo,jdbcType=INTEGER},
  reg_date = #{regDate,jdbcType=TIMESTAMP}
where auth_in_charge_idx = #{authInChargeIdx,jdbcType=INTEGER}

mapper.insert:
insert into ${church}.auth_in_charge (auth_set_idx,manager_idx,group_no) select a2.auth_set_idx,ai.manager_idx,k2.no from ${church}.auth_in_charge ai inner join ${church}.auth_set a on ai.auth_set_idx = a.auth_set_idx inner join ${church}.auth_set a2 on a.year=#{year} and a2.year=#{nextYear} and a.auth_category_idx = a2.auth_category_idx and a.auth_set_kind = a2.auth_set_kind inner join ${church}.kGroup k on ai.group_no = k.no inner join ${church}.kGroup k2 on k.year = #{year} and k2.year = #{nextYear} and k.depth1_name = k2.depth1_name and k.depth2_name = k2.depth2_name and k.depth3_name = k2.depth3_name and k.depth4_name = k2.depth4_name and k.depth5_name = k2.depth5_name
unix_timestamp(str_to_date(#{standardDate},'%Y-%m-%d')) between k.start_date and k.end_date
and k.k_part_idx = #{kPartIdx}
